import React, { useState, useEffect } from 'react';
import { 
    Table, 
    TableBody, 
    TableCell, 
    TableContainer, 
    TableHead, 
    TableRow, 
    Paper,
    Button 
} from '@mui/material';
import axios from 'axios';
import { format } from 'date-fns';

const TaskList = ({ taskTypeId }) => {
    const [tasks, setTasks] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchTasks = async () => {
            try {
                const token = localStorage.getItem('token'); // Lấy token từ localStorage
                const response = await axios.get(
                    `http://localhost:3000/api/tasks/by-type/${taskTypeId}`,
                    {
                        headers: {
                            Authorization: `Bearer ${token}`
                        }
                    }
                );
                setTasks(response.data.tasks);
                setLoading(false);
            } catch (err) {
                setError(err.message);
                setLoading(false);
            }
        };

        if (taskTypeId) {
            fetchTasks();
        }
    }, [taskTypeId]);

    if (loading) return <div>Đang tải...</div>;
    if (error) return <div>Có lỗi xảy ra: {error}</div>;

    return (
        <TableContainer component={Paper}>
            <Table>
                <TableHead>
                    <TableRow>
                        <TableCell>Tên công việc</TableCell>
                        <TableCell>Trạng thái</TableCell>
                        <TableCell>Người tạo</TableCell>
                        <TableCell>Ngày bắt đầu</TableCell>
                        <TableCell>Thời gian bắt đầu</TableCell>
                        <TableCell>Thời gian kết thúc</TableCell>
                    </TableRow>
                </TableHead>
                <TableBody>
                    {tasks.map((task) => (
                        <TableRow key={task.task_uid}>
                            <TableCell>{task.task_name}</TableCell>
                            <TableCell>{task.status}</TableCell>
                            <TableCell>{task.created_by}</TableCell>
                            <TableCell>
                                {format(new Date(task.date_begin), 'dd/MM/yyyy')}
                            </TableCell>
                            <TableCell>{task.start_time}</TableCell>
                            <TableCell>{task.end_time}</TableCell>
                        </TableRow>
                    ))}
                </TableBody>
            </Table>
        </TableContainer>
    );
};

export default TaskList;
