import React from 'react';
import { Box, Grid } from '@mui/material';

const Component3 = () => {
  return (
    <Box sx={{ flex: 1 }}>
      {/* Logo */}
      <Box
        sx={{
          width: '70%',
          margin: '0 auto 20px',
          height: 80,
          backgroundImage: 'url(/path/to/logo.png)', // Đường dẫn tới logo
          backgroundSize: 'contain',
          backgroundRepeat: 'no-repeat',
          backgroundPosition: 'center',
        }}
      />

      {/* Grid các ô */}
      <Grid
        container
        spacing={2} // Giảm khoảng cách giữa các hàng và cột
        sx={{
          flex: 1,
          alignItems: 'center',
          justifyContent: 'center',
        }}
      >
        {[...Array(6)].map((_, index) => (
          <Grid item xs={4} sm={4} key={index}>
            <Box
              sx={{
                width: '100%',
                height: 140, // Giảm chiều cao để tránh khoảng cách quá xa
                bgcolor: '#e0e0e0',
                borderRadius: '10px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: 18,
                fontWeight: 'bold',
              }}
            >
              Ô {index + 1}
            </Box>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default Component3;
