import React from "react";
import { Box, Typography, Table, TableBody, TableRow, TableCell, Paper } from "@mui/material";

const InfoTab = ({ groupInfo, loading }) => {
  if (loading) {
    return <Typography>Đang tải thông tin...</Typography>;
  }

  if (!groupInfo) {
    return <Typography>Không tìm thấy thông tin nhóm</Typography>;
  }

  return (
    <Box sx={{ width: '90%', margin: '0 auto' }}>
      <Typography variant="h5" mb={3} align="center" sx={{ color: '#1976d2', fontWeight: 'bold' }}>
        Group Information
      </Typography>
      <Paper elevation={2}>
        <Table size="small">
          <TableBody>
            <TableRow hover>
              <TableCell sx={{ fontWeight: 'bold', width: '30%' }}>Name</TableCell>
              <TableCell>{groupInfo.group_name}</TableCell>
            </TableRow>
            <TableRow hover>
              <TableCell sx={{ fontWeight: 'bold' }}>Created by</TableCell>
              <TableCell>{groupInfo.leader_name}</TableCell>
            </TableRow>
            <TableRow hover>
              <TableCell sx={{ fontWeight: 'bold' }}>Created at</TableCell>
              <TableCell>
                {new Date(groupInfo.creation_date).toLocaleDateString()} {groupInfo.time_created}
              </TableCell>
            </TableRow>
            <TableRow hover>
              <TableCell sx={{ fontWeight: 'bold' }}>Number of members</TableCell>
              <TableCell>{groupInfo.member_count}</TableCell>
            </TableRow>
            <TableRow hover>
              <TableCell sx={{ fontWeight: 'bold' }}>Description</TableCell>
              <TableCell>{groupInfo.description || 'No description'}</TableCell>
            </TableRow>
          </TableBody>
        </Table>
      </Paper>
    </Box>
  );
};

export default InfoTab;
