import React, { useState } from 'react';
import { Box, IconButton } from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import RemoveIcon from '@mui/icons-material/Remove';

const Component1 = () => {
  const [groups, setGroups] = useState([1]); // Bắt đầu với 1 group

  const handleAddGroup = () => {
    setGroups((prev) => [...prev, prev.length + 1]); // Thêm group mới
  };

  const handleRemoveGroup = () => {
    setGroups((prev) => (prev.length > 1 ? prev.slice(0, -1) : prev)); // Xóa group cuối cùng, đảm bảo còn ít nhất 1 group
  };

  return (
    <Box
      sx={{
        width: '250px', // Giới hạn chiều rộng
        height: '100vh', // Chiếm toàn bộ chiều cao màn hình
        bgcolor: '#f5f5f5',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        p: 2,
        border: '1px solid #ccc', // Viền bên ngoài
        borderRadius: 1,
      }}
    >
      {/* Avatar hình tròn */}
      <Box
        sx={{
          width: 50,
          height: 50,
          borderRadius: '50%',
          bgcolor: '#bbb',
          mb: 2,
        }}
      ></Box>

      {/* Nút + và - ở góc bên trái */}
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'flex-start', // Căn trái
          width: '100%',
          gap: 1,
          mb: 2, // Khoảng cách giữa các nút và danh sách nhóm
        }}
      >
        <IconButton onClick={handleAddGroup} color="primary">
          <AddIcon />
        </IconButton>
        <IconButton onClick={handleRemoveGroup} color="error">
          <RemoveIcon />
        </IconButton>
      </Box>

      {/* Danh sách các ô group */}
      <Box
        sx={{
          width: '100%',
          bgcolor: '#e0e0e0',
          p: 1,
          display: 'flex',
          flexDirection: 'column',
          gap: 2,
          overflowY: 'scroll', // Thanh cuộn luôn hiển thị khi cần
          maxHeight: 'calc(100vh - 150px)', // Giới hạn chiều cao danh sách nhóm với khoảng trống còn lại
          paddingRight: '5px', // Đảm bảo có không gian cho thanh cuộn
        }}
      >
        {groups.map((item) => (
          <Box
            key={item}
            sx={{
              width: '100%',
              height: 50, // Chiều cao của mỗi ô group
              bgcolor: '#ddd',
              borderRadius: 1,
            }}
          ></Box>
        ))}
      </Box>
    </Box>
  );
};

export default Component1;
