import React, { useState, useEffect } from 'react';
import { Box, Typography, Grid, Paper, IconButton } from '@mui/material';
import { ChevronLeft, ChevronRight } from '@mui/icons-material';
import api from '../utils/api';

const Calendar = () => {
  const [selectedDate, setSelectedDate] = useState(null);
  const [currentMonth, setCurrentMonth] = useState(new Date().getMonth());
  const [currentYear, setCurrentYear] = useState(new Date().getFullYear());
  const [tasks, setTasks] = useState([]);

  // Hàm lấy tasks từ API
  const fetchTasks = async () => {
    try {
      const data = await api.getUserTasksByMonth(currentYear, currentMonth + 1);
      setTasks(data);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, [currentMonth, currentYear]);

  // Kiểm tra ngày có nằm trong khoảng date_begin và date_end của task nào không
  const hasEvents = (day) => {
    const currentDate = new Date(currentYear, currentMonth, day);
    return tasks.some(task => {
      const beginDate = new Date(task.date_begin);
      const endDate = new Date(task.date_end);
      return currentDate >= beginDate && currentDate <= endDate;
    });
  };

  // Lấy tasks cho ngày được chọn
  const getTasksForDate = (day) => {
    const currentDate = new Date(currentYear, currentMonth, day);
    return tasks.filter(task => {
      const beginDate = new Date(task.date_begin);
      const endDate = new Date(task.date_end);
      return currentDate >= beginDate && currentDate <= endDate;
    });
  };

  // Giữ nguyên các hàm xử lý tháng và tính ngày
  const handleMonthChange = (direction) => {
    let newMonth = currentMonth + direction;
    let newYear = currentYear;

    if (newMonth < 0) {
      newMonth = 11;
      newYear -= 1;
    } else if (newMonth > 11) {
      newMonth = 0;
      newYear += 1;
    }

    setCurrentMonth(newMonth);
    setCurrentYear(newYear);
    setSelectedDate(null);
  };

  const getDaysInMonth = (month, year) => {
    return new Date(year, month + 1, 0).getDate();
  };

  return (
    <Box sx={{ width: '100%', maxWidth: '500px', margin: '0 auto', padding: 2 }}>
      {/* Giữ nguyên phần header tháng */}
      <Box sx={{
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        marginBottom: 2
      }}>
        <IconButton onClick={() => handleMonthChange(-1)}>
          <ChevronLeft />
        </IconButton>
        <Typography variant="h6" align="center">
          Tháng {currentMonth + 1} {currentYear}
        </Typography>
        <IconButton onClick={() => handleMonthChange(1)}>
          <ChevronRight />
        </IconButton>
      </Box>

      {/* Lưới ngày trong tháng */}
      <Grid container spacing={1}>
        {Array.from({ length: getDaysInMonth(currentMonth, currentYear) }).map((_, index) => {
          const day = index + 1;
          return (
            <Grid item xs={1.7} key={day}>
              <Box
                onClick={() => setSelectedDate(day)}
                sx={{
                  width: '40px',
                  height: '40px',
                  display: 'flex',
                  justifyContent: 'center',
                  alignItems: 'center',
                  borderRadius: '50%',
                  backgroundColor: day === selectedDate
                    ? '#d32f2f'
                    : hasEvents(day)
                      ? '#29b6f6'
                      : '#f5f5f5',
                  color: day === selectedDate ? '#fff' : '#000',
                  cursor: 'pointer',
                }}
              >
                {day}
              </Box>
            </Grid>
          );
        })}
      </Grid>

      {/* Hiển thị tasks của ngày được chọn */}
      {selectedDate && (
        <Box sx={{ marginTop: 3 }}>
          <Typography variant="h6">
            Ngày {selectedDate} tháng {currentMonth + 1} năm {currentYear}
          </Typography>
          
          {getTasksForDate(selectedDate).length > 0 ? (
            getTasksForDate(selectedDate).map((task, index) => (
              <Paper
                key={index}
                elevation={2}
                sx={{
                  padding: 2,
                  marginTop: 2,
                  backgroundColor: '#f9f9f9',
                  borderRadius: '8px'
                }}
              >
                <Typography variant="body1" fontWeight="bold">
                  {task.start_time?.substring(0, 5)} - {task.end_time?.substring(0, 5)}
                </Typography>
                <Typography variant="body2">
                  {task.task_uname}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {task.task_description}
                </Typography>
                <Typography variant="caption" color="text.secondary">
                  {task.task_type_name}
                </Typography>
              </Paper>
            ))
          ) : (
            <Typography sx={{ marginTop: 2 }}>Không có công việc.</Typography>
          )}
        </Box>
      )}
    </Box>
  );
};

export default Calendar;
