import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate
import './style.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faGooglePlusG, faFacebookF, faGithub, faLinkedinIn } from '@fortawesome/free-brands-svg-icons';
import axios from 'axios'; // Import axios

const AuthForm = ({ isLogin, setIsLogin }) => {
    const [isActive, setIsActive] = useState(isLogin);
    const navigate = useNavigate(); // Tạo hàm điều hướng
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [userName, setUserName] = useState('');

    useEffect(() => {
        setIsActive(isLogin);
    }, [isLogin]);

    const handleRegisterClick = () => {
        setIsLogin(false); // Chuyển sang trạng thái đăng ký
        setIsActive(true); // Kích hoạt form đăng ký
    };

    const handleLoginClick = () => {
        setIsLogin(true); // Chuyển sang trạng thái đăng nhập
        setIsActive(false); // Kích hoạt form đăng nhập
    };

    const handleLoginSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:3000/api/auth/login', {
                user_name: email, // Sử dụng email làm user_name
                password: password,
            });
            console.log('Login successful:', response.data);
            // Lưu token vào localStorage (hoặc sử dụng state quản lý token)
            localStorage.setItem('token', response.data.token);
            navigate('/menu'); // Chuyển hướng đến MenuLayout sau khi đăng nhập thành công
        } catch (error) {
            console.error('Login failed:', error.response.data);
            alert('Login failed: ' + error.response.data.message);
        }
    };

    const handleRegisterSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:3000/api/register/register', {
                user_name: userName,
                email: email,
                password: password,
            });
            console.log('Registration successful:', response.data);
            alert('Registration successful');
            handleLoginClick(); // Chuyển sang trang đăng nhập sau khi đăng ký thành công
        } catch (error) {
            console.error('Registration failed:', error.response.data);
            alert('Registration failed: ' + error.response.data.message);
        }
    };

    return (
        <div className={`container ${isActive ? 'active' : ''}`} id="container">
            <div className="form-container sign-up" style={{ display: isActive ? 'block' : 'none' }}>
                <form onSubmit={handleRegisterSubmit}>
                    <h1>Create Account</h1>
                    <div className="social-icons">
                        <a href="#" className="icon"><FontAwesomeIcon icon={faGooglePlusG} /></a>
                        <a href="#" className="icon"><FontAwesomeIcon icon={faFacebookF} /></a>
                        <a href="#" className="icon"><FontAwesomeIcon icon={faGithub} /></a>
                        <a href="#" className="icon"><FontAwesomeIcon icon={faLinkedinIn} /></a>
                    </div>
                    <span>or use your email for registration</span>
                    <input 
                        type="text" 
                        placeholder="Name" 
                        value={userName} 
                        onChange={(e) => setUserName(e.target.value)} 
                    />
                    <input 
                        type="email" 
                        placeholder="Email" 
                        value={email} 
                        onChange={(e) => setEmail(e.target.value)} 
                    />
                    <input 
                        type="password" 
                        placeholder="Password" 
                        value={password} 
                        onChange={(e) => setPassword(e.target.value)} 
                    />
                    <button type="submit">Sign Up</button>
                </form>
            </div>

            <div className="form-container sign-in" style={{ display: isActive ? 'none' : 'block' }}>
                <form onSubmit={handleLoginSubmit}>
                    <h1>Sign In</h1>
                    <div className="social-icons">
                        <a href="#" className="icon"><FontAwesomeIcon icon={faGooglePlusG} /></a>
                        <a href="#" className="icon"><FontAwesomeIcon icon={faFacebookF} /></a>
                        <a href="#" className="icon"><FontAwesomeIcon icon={faGithub} /></a>
                        <a href="#" className="icon"><FontAwesomeIcon icon={faLinkedinIn} /></a>
                    </div>
                    <span>or use your email password</span>
                    <input 
                        type="email" 
                        placeholder="Email" 
                        value={email} 
                        onChange={(e) => setEmail(e.target.value)} 
                    />
                    <input 
                        type="password" 
                        placeholder="Password" 
                        value={password} 
                        onChange={(e) => setPassword(e.target.value)} 
                    />
                    <a href="#">Forget Your Password?</a>
                    <button type="submit">Sign In</button>
                </form>
            </div>

            <div className="toggle-container">
                <div className="toggle">
                    <div className="toggle-panel toggle-left">
                        <h1>Welcome Back!</h1>
                        <p>Enter your personal details to use all of site features</p>
                        <button className="hidden" onClick={handleLoginClick}>Sign In</button>
                    </div>
                    <div className="toggle-panel toggle-right">
                        <h1>Hello, Friend!</h1>
                        <p>Register with your personal details to use all of site features</p>
                        <button className="hidden" onClick={handleRegisterClick}>Sign Up</button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AuthForm;
