import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './style.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faGooglePlusG, faFacebookF, faGithub, faLinkedinIn } from '@fortawesome/free-brands-svg-icons';

const AuthForm = ({ isLogin, setIsLogin }) => {
    const [isActive, setIsActive] = useState(!isLogin); // Trang mặc định
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [name, setName] = useState('');
    const [loading, setLoading] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        setIsActive(!isLogin);
    }, [isLogin]);

    const toggleForm = () => {
        setErrorMessage('');
        setIsLogin((prev) => !prev);
        setIsActive((prev) => !prev);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setErrorMessage('');

        try {
            if (isLogin) {
                // Đăng nhập
                const response = await axios.post('http://localhost:3000/api/auth/login', { email, password });
                if (response.data.token) {
                    localStorage.setItem('token', response.data.token);
                    navigate('/menu');
                } else {
                    setErrorMessage('Invalid credentials. Please try again.');
                }
            } else {
                // Đăng ký
                const response = await axios.post('http://localhost:3000/api/auth/register', { name, email, password });
                if (response.data.user) {
                    alert('Registration successful! Please log in.');
                    toggleForm(); // Chuyển sang form đăng nhập
                } else {
                    setErrorMessage('Registration failed. Please try again.');
                }
            }
        } catch (error) {
            if (error.response) {
                setErrorMessage(error.response.data.message || 'Server error occurred.');
            } else {
                setErrorMessage('Network error. Please try again later.');
            }
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className={`container ${isActive ? 'active' : ''}`} id="container">
            <div className={`form-container sign-up ${isActive ? 'show' : 'hide'}`}>
                <form onSubmit={handleSubmit}>
                    <h1>Create Account</h1>
                    <div className="social-icons">
                        <FontAwesomeIcon icon={faGooglePlusG} className="icon" />
                        <FontAwesomeIcon icon={faFacebookF} className="icon" />
                        <FontAwesomeIcon icon={faGithub} className="icon" />
                        <FontAwesomeIcon icon={faLinkedinIn} className="icon" />
                    </div>
                    <span>or use your email for registration</span>
                    <input
                        type="text"
                        placeholder="Name"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required={!isLogin}
                    />
                    <input
                        type="email"
                        placeholder="Email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                    <input
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                    {errorMessage && <p className="error-message">{errorMessage}</p>}
                    <button type="submit" disabled={loading}>
                        {loading ? 'Processing...' : 'Sign Up'}
                    </button>
                </form>
            </div>

            <div className={`form-container sign-in ${!isActive ? 'show' : 'hide'}`}>
                <form onSubmit={handleSubmit}>
                    <h1>Sign In</h1>
                    <div className="social-icons">
                        <FontAwesomeIcon icon={faGooglePlusG} className="icon" />
                        <FontAwesomeIcon icon={faFacebookF} className="icon" />
                        <FontAwesomeIcon icon={faGithub} className="icon" />
                        <FontAwesomeIcon icon={faLinkedinIn} className="icon" />
                    </div>
                    <span>or use your email and password</span>
                    <input
                        type="email"
                        placeholder="Email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                    <input
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                    {errorMessage && <p className="error-message">{errorMessage}</p>}
                    <button type="submit" disabled={loading}>
                        {loading ? 'Processing...' : 'Sign In'}
                    </button>
                </form>
            </div>

            <div className="toggle-container">
                <div className="toggle-panel toggle-left">
                    <h1>Welcome Back!</h1>
                    <p>Enter your details and start exploring!</p>
                    <button onClick={toggleForm}>Sign In</button>
                </div>
                <div className="toggle-panel toggle-right">
                    <h1>Hello, Friend!</h1>
                    <p>Sign up to explore more features!</p>
                    <button onClick={toggleForm}>Sign Up</button>
                </div>
            </div>
        </div>
    );
};

export default AuthForm;
