import React from 'react';
import { Box, Avatar, Typography, Grid } from '@mui/material';

const AvatarCircleWithRectangles = () => {
  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'flex-start', padding: 4 }}>
      {/* Avatar hình tròn */}
      <Avatar
        src="https://via.placeholder.com/150"
        alt="User Avatar"
        sx={{
          width: 100,
          height: 100,
          marginBottom: 2,
          border: '2px solid #1976d2',
        }}
      />
      <Typography variant="h6" sx={{ marginBottom: 4 }}>
        User Name
      </Typography>

      {/* Các ô chữ nhật */}
      <Grid
        container
        spacing={2}
        sx={{
          justifyContent: 'flex-start', // Bố trí các ô về phía trái
        }}
      >
        {Array.from({ length: 4 }).map((_, index) => (
          <Grid
            item
            xs={12} // Các ô box xếp thành hàng dọc
            sm={6}  // Trên màn hình rộng, mỗi ô chiếm 1/2 chiều rộng
            key={index}
          >
            <Box
              sx={{
                backgroundColor: '#1976d2',
                color: '#fff',
                padding: 2,
                borderRadius: 2,
                textAlign: 'left', // Văn bản trong ô căn trái
                minWidth: 100,
              }}
            >
              <Typography variant="body1">Box {index + 1}</Typography>
            </Box>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default AvatarCircleWithRectangles;
