import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import AuthForm from './sign_in/AuthForm';
import MenuLayout from './menu/MenuLayout';
import UserProfile from './user_info/user_menu';
import './sign_in/style.css';

function App() {
    const [isLogin, setIsLogin] = useState(false);

    useEffect(() => {
        const loginStatus = localStorage.getItem('isLogin');
        if (loginStatus === 'true') {
            setIsLogin(true);
        }
    }, []);

    const handleLoginSuccess = () => {
        setIsLogin(true);
        localStorage.setItem('isLogin', 'true');
    };

    const handleLogout = () => {
        setIsLogin(false);
        localStorage.removeItem('isLogin');
        localStorage.removeItem('token');
    };

    return (
        <Router>
            <Routes>
                <Route 
                    path="/" 
                    element={<AuthForm isLogin={isLogin} setIsLogin={handleLoginSuccess} />} 
                />
                <Route 
                    path="/menu" 
                    element={isLogin ? <MenuLayout onLogout={handleLogout} /> : <Navigate to="/" />} 
                />
                <Route 
                    path="/user" 
                    element={<UserProfile open={true} onClose={() => {}} onLogout={handleLogout} />} 
                />
            </Routes>
        </Router>
    );
}

export default App;
