import React, { useState } from 'react';
import { Box, IconButton, Menu, MenuItem } from '@mui/material';
import AddIcon from '@mui/icons-material/Add';

const Component1 = () => {
  const [groups, setGroups] = useState([1]);
  const [contextMenu, setContextMenu] = useState(null);
  const [selectedIndex, setSelectedIndex] = useState(null);

  const handleAddGroup = () => {
    setGroups((prev) => [...prev, prev.length + 1]);
  };

  const handleContextMenu = (event, index) => {
    event.preventDefault();
    setSelectedIndex(index); // Lưu chỉ mục của ô được chọn
    setContextMenu({
      mouseX: event.clientX + 2,
      mouseY: event.clientY - 6,
    });
  };

  const handleClose = () => {
    setContextMenu(null);
    setSelectedIndex(null);
  };

  const handleRemoveGroup = () => {
    setGroups((prev) => prev.filter((_, i) => i !== selectedIndex));
    handleClose();
  };

  return (
    <Box
      sx={{
        width: '250px',
        height: '100vh',
        bgcolor: '#f5f5f5',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        p: 2,
        border: '1px solid #ccc',
        borderRadius: 1,
      }}
    >
      {/* Avatar hình tròn với ảnh nền */}
      <Box
        sx={{
          width: 0,
          height: 50,
          borderRadius: '50%',
          backgroundImage: 'url("/todo.png")',
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          mb: 2,
        }}
      ></Box>

      {/* Nút + để thêm nhóm */}
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'flex-start',
          width: '100%',
          gap: 1,
          mb: 2,
        }}
      >
        <IconButton onClick={handleAddGroup} color="primary">
          <AddIcon />
        </IconButton>
      </Box>

      {/* Danh sách các ô group */}
      <Box
        sx={{
          width: '100%',
          bgcolor: '#e0e0e0',
          p: 1,
          display: 'flex',
          flexDirection: 'column',
          gap: 2,
          overflowY: 'auto',
          maxHeight: 'calc(100vh - 150px)',
          '&::-webkit-scrollbar': {
            width: '8px',
          },
          '&::-webkit-scrollbar-thumb': {
            backgroundColor: '#888',
            borderRadius: '4px',
          },
          '&::-webkit-scrollbar-thumb:hover': {
            backgroundColor: '#555',
          },
        }}
      >
        {groups.map((item, index) => (
          <Box
            key={item}
            onContextMenu={(e) => handleContextMenu(e, index)}
            sx={{
              width: '100%',
              height: 50,
              bgcolor: '#ddd',
              borderRadius: 1,
              flexShrink: 0,
              cursor: 'pointer',
            }}
          ></Box>
        ))}
      </Box>

      {/* Menu ngữ cảnh */}
      <Menu
        open={contextMenu !== null}
        onClose={handleClose}
        anchorReference="anchorPosition"
        anchorPosition={
          contextMenu !== null
            ? { top: contextMenu.mouseY, left: contextMenu.mouseX }
            : undefined
        }
      >
        <MenuItem onClick={handleRemoveGroup}>Xóa</MenuItem>
      </Menu>
    </Box>
  );
};

export default Component1;
