import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  Typography, Container, Box, Paper, Avatar, Divider, 
  Button, Dialog, DialogContent, DialogTitle, IconButton, 
  List, ListItem, ListItemAvatar, ListItemText,
  TextField, CircularProgress
} from '@mui/material';
import PersonIcon from '@mui/icons-material/Person';
import LockIcon from '@mui/icons-material/Lock';
import ExitToAppIcon from '@mui/icons-material/ExitToApp';
import CloseIcon from '@mui/icons-material/Close';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import api from '../utils/api';
import axios from 'axios';

const UserProfile = ({ open, onClose, onLogout, user, sx, BackdropProps, PaperProps }) => {
  return (
    <Dialog
      open={open}
      onClose={onClose}
      sx={sx}
      BackdropProps={BackdropProps}
      PaperProps={PaperProps}
    >
      <DialogTitle sx={{ m: 0, p: 2, minWidth: 300 }}>
        <Typography variant="h6">Thông tin tài khoản</Typography>
        <IconButton
          aria-label="close"
          onClick={onClose}
          sx={{
            position: 'absolute',
            right: 8,
            top: 8,
            color: (theme) => theme.palette.grey[500],
          }}
        >
          <CloseIcon />
        </IconButton>
      </DialogTitle>
      
      <DialogContent dividers>
        <List sx={{ width: '100%', bgcolor: 'background.paper' }}>
          <ListItem>
            <ListItemAvatar>
              <Avatar>
                <PersonIcon />
              </Avatar>
            </ListItemAvatar>
            <ListItemText 
              primary="Tên người dùng"
              secondary={user?.username || 'Chưa có tên'} 
            />
          </ListItem>

          <Divider variant="inset" component="li" />
          
          <ListItem>
            <ListItemAvatar>
              <Avatar>
                <LockIcon />
              </Avatar>
            </ListItemAvatar>
            <ListItemText 
              primary="Email"
              secondary={user?.email || 'Chưa có email'} 
            />
          </ListItem>

          <Divider variant="inset" component="li" />

          <ListItem>
            <Button
              fullWidth
              variant="contained"
              color="error"
              startIcon={<ExitToAppIcon />}
              onClick={onLogout}
              sx={{ mt: 1 }}
            >
              Đăng xuất
            </Button>
          </ListItem>
        </List>
      </DialogContent>
    </Dialog>
  );
};

export default UserProfile;
