import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; 
import axios from 'axios'; // Import axios để thực hiện yêu cầu HTTP
import './style.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faGooglePlusG, faFacebookF, faGithub, faLinkedinIn } from '@fortawesome/free-brands-svg-icons';

const AuthForm = ({ isLogin, setIsLogin }) => {
    const [isActive, setIsActive] = useState(!isLogin); // Đặt mặc định là trang đăng ký
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [name, setName] = useState(''); // Dành cho form đăng ký
    const navigate = useNavigate(); 

    useEffect(() => {
        setIsActive(!isLogin); // Chuyển đổi giữa form đăng ký và đăng nhập
    }, [isLogin]);

    const handleRegisterClick = () => {
        setIsLogin(false); // Chuyển sang trạng thái đăng ký
        setIsActive(true); // Kích hoạt form đăng ký
    };

    const handleLoginClick = () => {
        setIsLogin(true); // Chuyển sang trạng thái đăng nhập
        setIsActive(false); // Kích hoạt form đăng nhập
    };

    const handleSubmitLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:3000/api/auth/login', { email, password });
            if (response.data.token) {
                // Nếu đăng nhập thành công, chuyển hướng đến menu và lưu token vào localStorage
                localStorage.setItem('token', response.data.token);
                navigate('/menu');
            } else {
                alert('Invalid credentials');
            }
        } catch (error) {
            console.error('Error logging in:', error);
            alert('An error occurred during login');
        }
    };

    const handleSubmitRegister = async (e) => {
    e.preventDefault();
    try {
        const response = await axios.post('http://localhost:3000/api/auth/register', { name, email, password });
        if (response.data.user) {
            // Sau khi đăng ký thành công, chuyển sang đăng nhập
            alert('Registration successful, please log in');
            setIsLogin(true);
            setIsActive(false);
        } else {
            alert('Registration failed');
        }
    } catch (error) {
        console.error('Error registering:', error); // Log toàn bộ lỗi
        if (error.response) {
            // Nếu có phản hồi từ server, log chi tiết lỗi
            console.error('Backend error:', error.response.data);
            alert(`Error: ${error.response.data.message || 'An error occurred during registration'}`);
        } else {
            // Nếu không có phản hồi từ server (có thể là lỗi mạng)
            alert('Network error. Please try again later.');
        }
    }
};


    return (
        <div className={`container ${isActive ? 'active' : ''}`} id="container">
            {/* Form đăng ký */}
            <div className="form-container sign-up" style={{ display: isActive ? 'block' : 'none' }}>
                <form onSubmit={handleSubmitRegister}>
                    <h1>Create Account</h1>
                    <div className="social-icons">
                        <a href="#" className="icon"><FontAwesomeIcon icon={faGooglePlusG} /></a>
                        <a href="#" className="icon"><FontAwesomeIcon icon={faFacebookF} /></a>
                        <a href="#" className="icon"><FontAwesomeIcon icon={faGithub} /></a>
                        <a href="#" className="icon"><FontAwesomeIcon icon={faLinkedinIn} /></a>
                    </div>
                    <span>or use your email for registration</span>
                    <input 
                        type="text" 
                        placeholder="Name" 
                        value={name} 
                        onChange={(e) => setName(e.target.value)} 
                        required 
                    />
                    <input 
                        type="email" 
                        placeholder="Email" 
                        value={email} 
                        onChange={(e) => setEmail(e.target.value)} 
                        required 
                    />
                    <input 
                        type="password" 
                        placeholder="Password" 
                        value={password} 
                        onChange={(e) => setPassword(e.target.value)} 
                        required 
                    />
                    <button type="submit">Sign Up</button>
                </form>
            </div>

            {/* Form đăng nhập */}
            <div className="form-container sign-in" style={{ display: isActive ? 'none' : 'block' }}>
                <form onSubmit={handleSubmitLogin}>
                    <h1>Sign In</h1>
                    <div className="social-icons">
                        <a href="#" className="icon"><FontAwesomeIcon icon={faGooglePlusG} /></a>
                        <a href="#" className="icon"><FontAwesomeIcon icon={faFacebookF} /></a>
                        <a href="#" className="icon"><FontAwesomeIcon icon={faGithub} /></a>
                        <a href="#" className="icon"><FontAwesomeIcon icon={faLinkedinIn} /></a>
                    </div>
                    <span>or use your email and password</span>
                    <input 
                        type="email" 
                        placeholder="Email" 
                        value={email} 
                        onChange={(e) => setEmail(e.target.value)} 
                        required 
                    />
                    <input 
                        type="password" 
                        placeholder="Password" 
                        value={password} 
                        onChange={(e) => setPassword(e.target.value)} 
                        required 
                    />
                    <a href="#">Forget Your Password?</a>
                    <button type="submit">Sign In</button>
                </form>
            </div>

            {/* Toggle giữa đăng ký và đăng nhập */}
            <div className="toggle-container">
                <div className="toggle">
                    <div className="toggle-panel toggle-left">
                        <h1>Welcome Back!</h1>
                        <p>Enter your personal details to use all of site features</p>
                        <button className="hidden" onClick={handleLoginClick}>Sign In</button>
                    </div>
                    <div className="toggle-panel toggle-right">
                        <h1>Hello, Friend!</h1>
                        <p>Register with your personal details to use all of site features</p>
                        <button className="hidden" onClick={handleRegisterClick}>Sign Up</button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AuthForm;
